// src/pages/Home.jsx
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { financialAPI, expenseAPI } from '../services/api';
import { PieChart, Pie, Cell, ResponsiveContainer, Legend, Tooltip, BarChart, Bar, XAxis, YAxis, CartesianGrid } from 'recharts';
import './Home.css';

function Home() {
  const [overview, setOverview] = useState(null);
  const [summary, setSummary] = useState([]);
  const [monthlyStats, setMonthlyStats] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [overviewRes, summaryRes, monthlyRes] = await Promise.all([
        financialAPI.getOverview(),
        expenseAPI.getSummary(),
        expenseAPI.getMonthlyStats()
      ]);
      
      setOverview(overviewRes.data);
      setSummary(summaryRes.data);
      setMonthlyStats(monthlyRes.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div className="loading">Loading dashboard...</div>;
  }

  const COLORS = ['#667eea', '#764ba2', '#f093fb', '#4facfe', '#43e97b', '#fa709a', '#fee140', '#30cfd0'];

  const pieData = summary.map(item => ({
    name: item.category,
    value: item.total
  }));

  return (
    <div className="home-container">
      <div className="page-header">
        <h1>Dashboard</h1>
        <p>Your financial overview</p>
      </div>

      {/* Stats Cards */}
      <div className="stats-grid">
        <div className="stat-card income">
          <div className="stat-icon">ðŸ’°</div>
          <div className="stat-info">
            <h3>Total Income</h3>
            <p className="stat-value">${overview?.total_income?.toFixed(2) || 0}</p>
          </div>
        </div>

        <div className="stat-card expense">
          <div className="stat-icon">ðŸ’¸</div>
          <div className="stat-info">
            <h3>Total Expenses</h3>
            <p className="stat-value">${overview?.total_expenses?.toFixed(2) || 0}</p>
          </div>
        </div>

        <div className={`stat-card ${overview?.balance >= 0 ? 'positive' : 'negative'}`}>
          <div className="stat-icon">{overview?.balance >= 0 ? 'ðŸ“ˆ' : 'ðŸ“‰'}</div>
          <div className="stat-info">
            <h3>Balance</h3>
            <p className="stat-value">${overview?.balance?.toFixed(2) || 0}</p>
          </div>
        </div>

        <div className="stat-card savings">
          <div className="stat-icon">ðŸŽ¯</div>
          <div className="stat-info">
            <h3>Savings Rate</h3>
            <p className="stat-value">{overview?.savings_rate?.toFixed(1) || 0}%</p>
          </div>
        </div>
      </div>

      {/* Charts Section */}
      <div className="charts-grid">
        {/* Pie Chart - Expenses by Category */}
        <div className="chart-card">
          <h2>Expenses by Category</h2>
          {pieData.length > 0 ? (
            <ResponsiveContainer width="100%" height={300}>
              <PieChart>
                <Pie
                  data={pieData}
                  cx="50%"
                  cy="50%"
                  labelLine={false}
                  label={({ name, percent }) => `${name}: ${(percent * 100).toFixed(0)}%`}
                  outerRadius={80}
                  fill="#8884d8"
                  dataKey="value"
                >
                  {pieData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                  ))}
                </Pie>
                <Tooltip formatter={(value) => `$${value.toFixed(2)}`} />
                <Legend />
              </PieChart>
            </ResponsiveContainer>
          ) : (
            <p className="no-data">No expense data yet. Start tracking your expenses!</p>
          )}
        </div>

        {/* Bar Chart - Monthly Trends */}
        <div className="chart-card">
          <h2>Monthly Spending Trend</h2>
          {monthlyStats.length > 0 ? (
            <ResponsiveContainer width="100%" height={300}>
              <BarChart data={monthlyStats}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="month" />
                <YAxis />
                <Tooltip formatter={(value) => `$${value.toFixed(2)}`} />
                <Bar dataKey="total_expenses" fill="#667eea" />
              </BarChart>
            </ResponsiveContainer>
          ) : (
            <p className="no-data">No monthly data yet.</p>
          )}
        </div>
      </div>

      {/* Category Breakdown Table */}
      {summary.length > 0 && (
        <div className="table-card">
          <h2>Category Breakdown</h2>
          <table className="data-table">
            <thead>
              <tr>
                <th>Category</th>
                <th>Transactions</th>
                <th>Total Amount</th>
                <th>% of Total</th>
              </tr>
            </thead>
            <tbody>
              {summary.map((item, index) => {
                const percentage = overview?.total_expenses > 0 
                  ? (item.total / overview.total_expenses * 100).toFixed(1)
                  : 0;
                return (
                  <tr key={index}>
                    <td><strong>{item.category}</strong></td>
                    <td>{item.count}</td>
                    <td>${item.total.toFixed(2)}</td>
                    <td>{percentage}%</td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      )}

      {/* Quick Actions */}
      <div className="quick-actions">
        <Link to="/expenses" className="action-btn">
          âž• Add Expense
        </Link>
        <Link to="/income" className="action-btn">
          ðŸ’µ Add Income
        </Link>
        <Link to="/recommendations" className="action-btn">
          ðŸ’¡ View Recommendations
        </Link>
      </div>
    </div>
  );
}

export default Home;
